// sendRequests sends the one request per stationo, per measurement.
// The desired time is determined in combination of startTime in config and
// lastFetchedData in fetchedState.
function sendRequests(cfg, fetchedState) {
    var stats = {
        numSent: 0,
        numProcessed: 0,
    };

    for (let [stationId, data] of Object.entries(cfg.desiredStations)) {
        if (typeof data !== 'object' ) {
            throw 'incorrect config data for ' + stationId + '. Data must be object.';
        }

        var st = DateTime.fromISO(data.startTime);
        if (st.minute > 0 || st.second > 0 || st.millisecond > 0) {
            throw 'startTime must be whole hours.'
        }
        st = st.toUTC();

        let datasets = data.datasets;
        if (datasets.length == 1 && datasets[0] == 'all') {
            datasets = Object.keys(cfg.availableDatasets);
        }

        datasets.forEach(function(dataset, index) {
            stats.numProcessed++;

            var time = st;
            // Compare start time with lastFetchedTime
            var t = getLastedFetchTime(fetchedState, stationId, dataset);
            if (t >= time) {
                time = t;
            }

            r = new Request(stationId, dataset, time);
            isSent = sendRequest(r);
            if (isSent) {
                stats.numSent++;
            }
        });
    }

    return stats;
}

// Request is a object that contains request information used
// by the function sendRequest.
function Request(stationId, dataset, time) {
    this.stationId = stationId;
    this.dataset = dataset;
    this.time = DateTime.fromISO(time);
}

// sendRequest sends one request via node.send for a specific Request.
// Before sending does it check if given
// time can yield results. If not, will it do nothing.
function sendRequest(request) {
    if (request === undefined) {
        return false;
    }

    // Check if we can expect data. The data is available every whole hour.
    if (request.time >= DateTime.utc()) {
        return false;
    } else if (request.time.hour >= DateTime.utc().hour) {
        return false;
    }

    var hours = Math.ceil(request.time.diffNow().negate().as('hours'));

    // Minimum hour in the API is 3 hours.
    if (hours < 3) {
        hours = 3;
    }

    var requestData = {
        stationid: request.stationId,
        hours: hours,
        dataset: request.dataset,
    };

    msg.payload = requestData;
    msg._requestedData = requestData;
    node.send(msg);

    return true;
}

// getLastedFetchTime return last fetched time from the nested
// data structure of fetchedState. Requires stationId (sid) and
// dataset (d).
function getLastedFetchTime(fetchedState, sid, d) {
    if (!(sid in fetchedState)) {
        return null;
    }

    if (!(d in fetchedState[sid])) {
        return null;
    }

    var lastFetchedTime = fetchedState[sid][d].lastFetchedTime;
    if (lastFetchedTime === undefined) {
        return null;
    }

    return DateTime.fromISO(lastFetchedTime);
}

function updateFetchedState(fetchedState, msg) {
    if (msg._requestedData === undefined) {
        throw 'Missing msg._requestedData in feedback loop';
    }
    if (typeof msg._requestedData !== 'object') {
        throw 'msg._requestedData is not an object';
    }

    if (msg._receivedPayload === undefined) {
        throw 'Missing msg._receivedPayload in feedback loop';
    }
    if (typeof msg._receivedPayload !== 'object') {
        throw 'msg._receivedPayload is not an object';
    }

    var sid = msg._requestedData.stationid;
    if (sid === '') {
        throw 'msg._requestedData.stationid is empty';
    }

    var d = msg._requestedData.dataset;
    if (d === '') {
        throw 'msg._requestedData.dataset is empty';
    }

    var time = msg._receivedPayload.last_measurement;
    if (time === undefined) {
        throw 'msg._receivedPayload.last_measurement is empty';
    }
    time = DateTime.fromMillis(time);

    if (!(sid in fetchedState)) {
        fetchedState[sid] = {};
    }

    if (!(d in fetchedState[sid])) {
        fetchedState[sid][d] = {};
    }

    var request;

    var t = fetchedState[sid][d].lastFetchedTime;
    if (t === undefined) {
        fetchedState[sid][d].lastFetchedTime = time;
    } else {
        t = DateTime.fromISO(t);
        if (time > t) {
            fetchedState[sid][d].lastFetchedTime = time;
        }
    }

    return fetchedState;
}

